import React from 'react';
import { create } from '../services';

class LeftSide extends React.Component {
  constructor() {
    super();
    this.state = {
      validText: false,
    };
    this.validTextArea = this.validTextArea.bind(this);
    this.createCommentary = this.createCommentary.bind(this);
  }

  validTextArea({ target }) {
    const text = target.value;
    if (text.length > 15) {
      this.setState({ validText: true, text });
    } else {
      this.setState({ validText: false, text });
    }
  }

  createCommentary(name, cartoom) {
    create(name, cartoom);
    location.reload()
    return false;
  }

  render() {
    const { validText, text } = this.state
    return (
      <section className="left-side">
        <h4>Comment√°rio</h4>
        <div>
          <textarea
            maxLength="1000"
            onChange= { this.validTextArea }
          ></textarea>
          <button
            className="button"
            disabled = { !validText }
            onClick={ () => this.createCommentary(text, text) }
          >Cadastrar</button>
        </div>
      </section>
    );
  }
}

export default LeftSide;
